member.example:
 下記は、値を「ラジアン」から「度」に変換するための 
  の使用方法を示します。

using System;

using MapQuest.CoordinateSystems;

namespace UnitConversion
{
    class TestMain
    {
        [STAThread]
        static void Main(string[] args)
        {
            AngularUnitConverter converter = 
                new AngularUnitConverter(Unit.Radian, Unit.Degree);

            double angle  = Math.PI/2; // radian
            double result = converter.Convert(angle);

            Console.WriteLine(result.ToString());

            IAngularUnitConverter inverse = converter.GetInverse();

            angle = inverse.Convert(result);

            Console.WriteLine(angle.ToString());
        }
    }
}

 下記は、値を「センチメートル」から「ミリメートル」に変換するための 
  の使用方法を示します。

using System;

using MapQuest.CoordinateSystems;

namespace UnitConversion
{
    class TestMain
    {
        [STAThread]
        static void Main(string[] args)
        {
            LinearUnitConverter converter = 
                new LinearUnitConverter(Unit.Centimetre, Unit.Millimetre);

            double distance = 10; // cm
            double result   = converter.Convert(distance);

            Console.WriteLine(result.ToString());

            ILinearUnitConverter inverse = converter.GetInverse();

            distance = inverse.Convert(result);

            Console.WriteLine(distance.ToString());
        }
    }
}

このクラスの使用方法を示します。

using System;
using System.Collections;

using MapQuest.CoordinateSystems;

public class SamplesParameterList  
{

    public static void Main()  
    {
        // Creates and initializes a new ParameterList.
        ParameterList listParameters = new ParameterList();
        listParameters.Add("Braeburn Apples", "1.49" );
        listParameters.Add("Fuji Apples", "1.29" );
        listParameters.Add("Gala Apples", "1.49" );
        listParameters.Add("Golden Delicious Apples", "1.29");
        listParameters.Add("Granny Smith Apples", "0.89");
        listParameters.Add("Red Delicious Apples", "0.99");

        // Displays the values in the ParameterList.
        Console.WriteLine("Displays the elements using the IEnumerator:");
        PrintKeysAndValues(listParameters);

        // Copies the ParameterList to an array with DictionaryEntry elements.
        DictionaryEntry[] myArr = new DictionaryEntry[listParameters.Count];
        listParameters.CopyTo( myArr, 0 );

        // Displays the values in the array.
        Console.WriteLine( "Displays the elements in the array:" );
        Console.WriteLine( "   KEY                       VALUE" );
        for ( int i = 0; i < myArr.Length; i++ )
            Console.WriteLine("   {0,-25} {1}", myArr[i].Key, myArr[i].Value );
        Console.WriteLine();

        // Searches for a key.
        if (listParameters.Contains("Kiwis"))
            Console.WriteLine("The collection contains the key \"Kiwis\".");
        else
            Console.WriteLine("The collection does not contain the key \"Kiwis\".");
        Console.WriteLine();

        // Deletes a key.
        listParameters.Remove("Plums");
        Console.WriteLine("The collection contains the following elements after removing \"Plums\":" );
        PrintKeysAndValues(listParameters);

        // Clears the entire collection.
        listParameters.Clear();
        Console.WriteLine("The collection contains the following elements after it is cleared:" );
        PrintKeysAndValues(listParameters);
    }

    public static void PrintKeysAndValues(IEnumerable listParameters)  
    {
        IEnumerator myEnumerator = listParameters.GetEnumerator();
        Console.WriteLine("   KEY                       VALUE");
        foreach (DictionaryEntry de in listParameters)
            Console.WriteLine("   {0,-25} {1}", de.Key, de.Value);
        Console.WriteLine();
    }
}
